package com.songxiaocai.tradedata.aspect.section;/* * Description:com.songxiaocai.tradedata.aspect.test * @Date Create on 10:03 * @author <a href="mailto:yangyi@songxiaocai.com">yangyi</a> * @Version JDK 1.8 * @since version 1.0 Copyright 2019-02-25æ—¥SXC All Rights Reserved. */import com.alibaba.druid.pool.DruidDataSource;import com.songxiaocai.tradedata.test.TestService;import lombok.extern.slf4j.Slf4j;import org.aspectj.lang.ProceedingJoinPoint;import org.aspectj.lang.annotation.Around;import org.aspectj.lang.annotation.Aspect;import org.aspectj.lang.annotation.Pointcut;import org.springframework.stereotype.Component;@Component@Aspect@Slf4jpublic class DataSourceAspect {	@Around("weave()")	public Object Advice(ProceedingJoinPoint pjp ) throws Throwable{		DruidDataSource dds=(DruidDataSource)pjp.getTarget();		log.debug("DruidDataSource message "+"url="				+dds.getUrl()+" ; Password="+dds.getPassword()+				"  ;userName="+dds.getUsername());		Object obj=pjp.proceed();		return obj;	}	@Pointcut("execution(* com.alibaba.druid.pool.DruidDataSource.*(..))")	public void weave() { }}