package com.songxiaocai.tradedata.config.dataSource;/* * Description:com.songxiaocai.tradedata.config.dataSource * @Date Create on 10:43 * @author <a href="mailto:yangyi@songxiaocai.com">yangyi</a> * @Version JDK 1.8 * @since version 1.0 Copyright 2019-04-03日SXC All Rights Reserved. */import lombok.extern.slf4j.Slf4j;/*通过 TheadLocal 来保存每个线程选择哪个数据源的标志(key) */@Slf4jpublic class DataSourceTypeManager {	private static final ThreadLocal<DataSources> dataSourceTypes = new ThreadLocal<DataSources>(){		@Override		protected DataSources initialValue(){			return DataSources.testProd;		}	};	public static void remove(){		 dataSourceTypes.remove();	}	public static DataSources get(){		return dataSourceTypes.get();	}	public static void set(DataSources dataSourceType){			dataSourceTypes.set(dataSourceType);	}	public static void reset(){		dataSourceTypes.set(DataSources.testProd);	}}