package com.songxiaocai.tradedata.infrastructure.repository.impl;/* * Description:com.songxiaocai.tradedata.infrastructure.repository.impl * @Date Create on 15:05 * @author <a href="mailto:yangyi@songxiaocai.com">yangyi</a> * @Version JDK 1.8 * @since version 1.0 Copyright 2019-04-10æ—¥SXC All Rights Reserved. */import com.songxiaocai.tradedata.application.repository.SxcFixPriceBaseInfoRepository;import com.songxiaocai.tradedata.infrastructure.repository.entity.SxcFixPriceBaseInfo;import com.songxiaocai.tradedata.infrastructure.repository.entity.example.SxcFixPriceBaseInfoExample;import com.songxiaocai.tradedata.infrastructure.repository.mapper.SxcFixPriceBaseInfoMapper;import com.songxiaocai.tradedata.service.params.SxcFixPriceBaseInfoDTO;import lombok.extern.slf4j.Slf4j;import org.springframework.beans.BeanUtils;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Repository;import org.springframework.util.CollectionUtils;import java.util.List;@Repository@Slf4jpublic class SxcFixPriceBaseInfoRepositoryImpl implements SxcFixPriceBaseInfoRepository {	@Autowired	private SxcFixPriceBaseInfoMapper sxcFixPriceBaseInfoMapper;	@Override	public Boolean insertSelective(SxcFixPriceBaseInfo record) {		int num=sxcFixPriceBaseInfoMapper.insertSelective(record);		if(num<0){			return false;		}		return true;	}	@Override	public SxcFixPriceBaseInfoDTO selectByExample(SxcFixPriceBaseInfoExample condition) {		List<SxcFixPriceBaseInfo> list= sxcFixPriceBaseInfoMapper.selectByExample(condition);		if(CollectionUtils.isEmpty(list)){			return null;		}		SxcFixPriceBaseInfoDTO sxcFixPriceBaseInfoDTO=new SxcFixPriceBaseInfoDTO();		list.forEach(ss->{			BeanUtils.copyProperties(ss,sxcFixPriceBaseInfoDTO);		});		return sxcFixPriceBaseInfoDTO;	}	@Override	public Boolean updateByExampleSelective(SxcFixPriceBaseInfo record, SxcFixPriceBaseInfoExample condition) {		Integer num=sxcFixPriceBaseInfoMapper.updateByExampleSelective(record,condition);		if(num<0){			return false;		}		return true;	}}