package com.songxiaocai.tradedata.infrastructure.repository.impl;/* * Description:com.songxiaocai.tradedata.infrastructure.repository.impl * @Date Create on 17:45 * @author <a href="mailto:yangyi@songxiaocai.com">yangyi</a> * @Version JDK 1.8 * @since version 1.0 Copyright 2019-05-09æ—¥SXC All Rights Reserved. */import com.songxiaocai.tradedata.application.repository.SxcFixPriceRuleRepository;import com.songxiaocai.tradedata.infrastructure.repository.entity.SxcFixPriceRule;import com.songxiaocai.tradedata.infrastructure.repository.entity.example.SxcFixPriceRuleExample;import com.songxiaocai.tradedata.infrastructure.repository.mapper.SxcFixPriceRuleMapper;import com.songxiaocai.tradedata.service.params.SxcFixPriceRuleDTO;import lombok.extern.slf4j.Slf4j;import org.springframework.beans.BeanUtils;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Repository;import org.springframework.util.CollectionUtils;import java.util.List;@Repository@Slf4jpublic class SxcFixPriceRuleRepositoryImpl implements SxcFixPriceRuleRepository {	@Autowired	private SxcFixPriceRuleMapper sxcFixPriceRuleMapper;	@Override	public SxcFixPriceRuleDTO selectByExample(SxcFixPriceRuleExample example) {		List<SxcFixPriceRule> list= sxcFixPriceRuleMapper.selectByExample(example);		if(CollectionUtils.isEmpty(list)){			return null;		}		SxcFixPriceRuleDTO sxcFixPriceRuleDTO=new SxcFixPriceRuleDTO();		list.forEach(ss->{			BeanUtils.copyProperties(ss,sxcFixPriceRuleDTO);		});		return sxcFixPriceRuleDTO;	}	@Override	public Boolean updateByExampleSelective(SxcFixPriceRule record, SxcFixPriceRuleExample example) {		int num=sxcFixPriceRuleMapper.updateByExampleSelective(record,example);		if(num<0){			return false;		}		return true;	}}