package com.songxiaocai.tradedata.infrastructure.repository.impl;/* * Description:com.songxiaocai.tradedata.infrastructure.repository.impl * @Date Create on 17:56 * @author <a href="mailto:yangyi@songxiaocai.com">yangyi</a> * @Version JDK 1.8 * @since version 1.0 Copyright 2019-06-04æ—¥SXC All Rights Reserved. */import com.songxiaocai.tradedata.application.repository.SxcGoodsGroupRelationRepository;import com.songxiaocai.tradedata.infrastructure.repository.entity.SxcGoodsGroupRelation;import com.songxiaocai.tradedata.infrastructure.repository.entity.example.SxcGoodsGroupRelationExample;import com.songxiaocai.tradedata.infrastructure.repository.mapper.SxcGoodsGroupRelationMapper;import lombok.extern.slf4j.Slf4j;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Repository;import org.springframework.util.CollectionUtils;import java.util.List;@Slf4j@Repositorypublic class SxcGoodsGroupRelationRepositoryImpl implements SxcGoodsGroupRelationRepository {	@Autowired	SxcGoodsGroupRelationMapper sxcGoodsGroupRelationMapper;	@Override	public List<SxcGoodsGroupRelation> selectByExample(SxcGoodsGroupRelationExample example){		List<SxcGoodsGroupRelation> list=sxcGoodsGroupRelationMapper.selectByExample(example);		if(CollectionUtils.isEmpty(list)){			return null;		}		return list;	}}