package com.songxiaocai.tradedata.infrastructure.repository.impl;/* * Description:com.songxiaocai.tradedata.infrastructure.repository.impl * @Date Create on 17:14 * @author <a href="mailto:yangyi@songxiaocai.com">yangyi</a> * @Version JDK 1.8 * @since version 1.0 Copyright 2019-02-14æ—¥SXC All Rights Reserved. */import com.songxiaocai.tradedata.application.repository.SxcItemSkuRepository;import com.songxiaocai.tradedata.infrastructure.repository.entity.SxcItemSku;import com.songxiaocai.tradedata.infrastructure.repository.mapper.SxcItemSkuMapper;import lombok.extern.slf4j.Slf4j;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Repository;import org.springframework.util.CollectionUtils;import java.util.HashMap;import java.util.List;@Slf4j@Repositorypublic class SxcItemSkuRepositoryImpl implements SxcItemSkuRepository {	@Autowired	private SxcItemSkuMapper sxcItemSkuMapper;	@Override	public List<SxcItemSku> getByItemId(Integer ItemId) {		List<SxcItemSku> ss=sxcItemSkuMapper.getByItemId(ItemId);		if(CollectionUtils.isEmpty(ss)){			return null;		}		return ss;	}	@Override	public int modifyCutffTime(String features, Integer ItemId) {		log.info("ItemId="+ItemId);		HashMap<String,Object> map=new HashMap();		map.put("features",features);		map.put("ItemId",ItemId);		int num=sxcItemSkuMapper.modifyCutffTime(map);		return num;	}}