package com.songxiaocai.tradedata.infrastructure.repository.impl;/* * Description:com.songxiaocai.tradedata.infrastructure.repository.impl * @Date Create on 11:25 * @author <a href="mailto:yangyi@songxiaocai.com">yangyi</a> * @Version JDK 1.8 * @since version 1.0 Copyright 2019-06-05æ—¥SXC All Rights Reserved. */import com.songxiaocai.tradedata.application.repository.SxcServiceRuleRepository;import com.songxiaocai.tradedata.infrastructure.repository.entity.SxcServiceRule;import com.songxiaocai.tradedata.infrastructure.repository.entity.example.SxcServiceRuleExample;import com.songxiaocai.tradedata.infrastructure.repository.mapper.SxcServiceRuleMapper;import lombok.extern.slf4j.Slf4j;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Repository;@Slf4j@Repositorypublic class SxcServiceRuleRepositoryImpl implements SxcServiceRuleRepository {	@Autowired	SxcServiceRuleMapper sxcServiceRuleMapper;	@Override	public Boolean updateByExampleSelective(SxcServiceRule record, SxcServiceRuleExample example) {		int num=sxcServiceRuleMapper.updateByExampleSelective(record,example);		if(num<0){			return false;		}		return true;	}}