package com.songxiaocai.tradedata.infrastructure.repository.impl;/* * Description:com.songxiaocai.tradedata.infrastructure.repository.impl * @Date Create on 17:14 * @author <a href="mailto:yangyi@songxiaocai.com">yangyi</a> * @Version JDK 1.8 * @since version 1.0 Copyright 2019-02-14æ—¥SXC All Rights Reserved. */import com.songxiaocai.tradedata.application.repository.SxcStorehouseRepository;import com.songxiaocai.tradedata.infrastructure.repository.entity.SxcStorehouse;import com.songxiaocai.tradedata.infrastructure.repository.mapper.SxcStorehouseMapper;import com.songxiaocai.tradedata.service.params.SxcStorehouseInfoDTO;import lombok.extern.slf4j.Slf4j;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Repository;import org.springframework.util.CollectionUtils;import java.util.HashMap;import java.util.List;@Slf4j@Repositorypublic class SxcStorehouseRepositoryImpl implements SxcStorehouseRepository {	@Autowired	private SxcStorehouseMapper sxcStorehouseMapper;	@Override	public List<SxcStorehouse> getById(Integer id) {		List<SxcStorehouse> ss=sxcStorehouseMapper.getById(id);		if(CollectionUtils.isEmpty(ss)){			return null;		}		return ss;	}	@Override	public int modifyCutffTime(String cutOffTime,Integer id) {		log.info("Storehouse id="+id);		HashMap<String,Object> map=new HashMap();		map.put("cutOffTime",cutOffTime);		map.put("id",id);		int num=sxcStorehouseMapper.modifyCutffTime(map);		return num;	}}