package com.songxiaocai.tradedata.tool.encr;/* * Description:com.songxiaocai.tradedata.tool.encr * @Date Create on 17:41 * @author <a href="mailto:yangyi@songxiaocai.com">yangyi</a> * @Version JDK 1.8 * @since version 1.0 Copyright 2019-04-30日SXC All Rights Reserved. */import java.security.MessageDigest;public class MD5Utils {	/**	 * 对字符串md5加密	 *	 * @param data	 * @return	 */	public static String getMD5(String data) {		String result = "";		try {			MessageDigest md5 = MessageDigest.getInstance("MD5");			md5.update(data.getBytes("ISO-8859-1"));			byte bytes[] = md5.digest();			for (int i = 0; i < bytes.length; i++) {				result += Integer.toHexString(						(0x000000ff & bytes[i]) | 0xffffff00).substring(6);			}		} catch (Exception ex) {			ex.printStackTrace();			return "";		}		return result;	}}