package com.songxiaocai.tradedata.tool.http;/* * Description:com.songxiaocai.tradedata.tool.http * @Date Create on 17:55 * @author <a href="mailto:yangyi@songxiaocai.com">yangyi</a> * @Version JDK 1.8 * @since version 1.0 Copyright 2019-02-25æ—¥SXC All Rights Reserved. */import org.apache.http.conn.HttpClientConnectionManager;public class ClearConnectionsHandler extends Thread{	private final HttpClientConnectionManager connMgr;	private volatile boolean shutdown;	public ClearConnectionsHandler(HttpClientConnectionManager connMgr) {		this.connMgr = connMgr;		this.start();	}	@Override	public void run() {		try {			while (!shutdown) {				synchronized (this) {					wait(5000);					connMgr.closeExpiredConnections();				}			}		} catch (InterruptedException ex) {		}	}	public void shutdown() {		shutdown = true;		synchronized (this) {			notifyAll();		}	}}