package com.songxiaocai.tradedata.tool.map;/* * Description:com.songxiaocai.tradedata.tool.map * @Date Create on 15:54 * @author <a href="mailto:yangyi@songxiaocai.com">yangyi</a> * @Version JDK 1.8 * @since version 1.0 Copyright 2019-02-26æ—¥SXC All Rights Reserved. */import java.util.Map;import java.util.Set;public class MapUtil {	public static String mapToString(Map<String,Object> map,String join){		String str=null;		StringBuilder sb=new StringBuilder();		Set<Map.Entry<String, Object>>ts= map.entrySet();		for (Map.Entry<String, Object> t:ts			 ) {			sb.append(t.getValue()).append(join);		}		sb.deleteCharAt(sb.toString().lastIndexOf(join));		return sb.toString();	}	public static String mapToRequestBody(Map<String,Object> map){		String str=null;		StringBuilder sb=new StringBuilder();		Set<Map.Entry<String, Object>>ts= map.entrySet();		for (Map.Entry<String, Object> t:ts		) {			sb.append(t.getKey()).append("=").append(t.getValue()).append("&");		}		sb.deleteCharAt(sb.toString().lastIndexOf("&"));		return sb.toString();	}	public static String mapToCookie(Map<String,String> map,String join){		String str=null;		StringBuilder sb=new StringBuilder();		Set<Map.Entry<String, String>>ts= map.entrySet();		for (Map.Entry<String, String> t:ts		) {			sb.append(t.getKey()).append("=").append(t.getValue()).append(join);		}		sb.deleteCharAt(sb.toString().lastIndexOf(join));		return sb.toString();	}}