package com.songxiaocai.tradedata.web.controller.web;/* * Description:com.songxiaocai.tradedata.web.controller.web * @Date Create on 13:46 * @author <a href="mailto:yangyi@songxiaocai.com">yangyi</a> * @Version JDK 1.8 * @since version 1.0 Copyright 2019-03-18日SXC All Rights Reserved. */import com.songxiaocai.tradedata.service.CutOrderService;import com.sxc.common.domain.result.Result;import lombok.extern.slf4j.Slf4j;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestMethod;import org.springframework.web.bind.annotation.ResponseBody;@Slf4j@Controller@RequestMapping("/api/tradedata/web")public class CutOrderController {	@Autowired	private CutOrderService cutOrderService;	/*	修改截单时间	 */	@RequestMapping(value = "/cutOrder",method = RequestMethod.GET)	@ResponseBody	public Result<?> sendCoupons(String mobile, Integer SkuId ) {		Result<String> re=cutOrderService.modifyCutOffTime(mobile,SkuId);		Result result=new Result();		if(!re.isSuccess()){			result.setData(false);			result.setErrorMessage(re.getErrorMessage());			result.setSuccess(false);			return result;		}		result.setData(re.getData());		result.setSuccess(true);		result.setSuccessInfo("修改截单时间成功");		return result;	}}