package com.songxiaocai.tradedata.web.controller.web;/* * Description:com.songxiaocai.tradedata.web.controller.web * @Date Create on 14:21 * @author <a href="mailto:yangyi@songxiaocai.com">yangyi</a> * @Version JDK 1.8 * @since version 1.0 Copyright 2019-05-16日SXC All Rights Reserved. */import com.songxiaocai.tradedata.service.FreightFeeService;import com.songxiaocai.tradedata.service.GoodsPriceService;import com.sxc.common.domain.result.Result;import lombok.extern.slf4j.Slf4j;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestMethod;import org.springframework.web.bind.annotation.ResponseBody;@Slf4j@Controller@RequestMapping("/api/tradedata/web")public class FreightFeeController {	@Autowired	private FreightFeeService freightFeeService;	@Autowired	private GoodsPriceService goodsPriceService;	@RequestMapping(value = "/freightFeeAndGoodsPrice",method = RequestMethod.POST)	@ResponseBody	public Result<?> modifyFreightFeeAndGoodsPrice(Integer ruleId, Boolean isFreight, String mobile, Integer ItemId, Long basePrice, Long raisePrice ) {		Result<String> reBasePrice=goodsPriceService.modifyBasePrice(ItemId,basePrice);		Result<String> reRaisePrice=goodsPriceService.modifyRaisePrice(ItemId,raisePrice);		Result<String> reFreightFee=freightFeeService.modifyFreightFee(ruleId,mobile,isFreight);		Result result=new Result();		if(!reBasePrice.isSuccess() || !reRaisePrice.isSuccess() || !reFreightFee.isSuccess()){			result.setErrorMessage("BasePrice="+reBasePrice.getErrorMessage()+"RaisePrice="+reRaisePrice.getErrorMessage()+"FreightFee="+reFreightFee.getErrorMessage());			result.setSuccess(false);			return result;		}		result.setData("BasePrice="+reBasePrice.getData()+"RaisePrice="+reRaisePrice.getData()+"FreightFee="+reFreightFee.getData());		result.setSuccess(true);		result.setSuccessInfo("修改下单支付价格1分钱成功");		return result;	}}