package com.songxiaocai.tradedata.web.controller.web;/* * Description:com.songxiaocai.tradedata.web.controller.web * @Date Create on 17:41 * @author <a href="mailto:yangyi@songxiaocai.com">yangyi</a> * @Version JDK 1.8 * @since version 1.0 Copyright 2019-04-2日SXC All Rights Reserved. */import com.songxiaocai.tradedata.service.GoodsgroupService;import com.sxc.common.domain.result.Result;import com.sxc.jotunheim.result.sku.DownSkuInfoDTO;import lombok.extern.slf4j.Slf4j;import org.apache.commons.lang3.StringUtils;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestMethod;import org.springframework.web.bind.annotation.ResponseBody;@Slf4j@Controller@RequestMapping("/api/tradedata/web")public class GoodsgroupController {	@Autowired	private GoodsgroupService goodsgroupService;	/*	skuid=5934,itemId=9478,属于云丰组	skuid=6478,itemId=10039,属于云果组	skuid=6733,itemId=10366,属于云果组与云丰组	 */	@RequestMapping(value = "/relationEdit",method = RequestMethod.POST)	@ResponseBody	public Result<?> relationEdit(String skuIds, String addGroupIds, String deleteGroupIds,String operateId) {		Result result=new Result();		if(StringUtils.isAllBlank(skuIds,addGroupIds,deleteGroupIds))		{			result.setSuccess(false);			result.setErrorMessage("请求参数为空！");			log.info(String.format("skuIds=%s, addGroupIds=%s,deleteGroupIds=%s, operateId=%s",  skuIds, addGroupIds,deleteGroupIds,operateId));			return result;		}		try {			Result<String> re = goodsgroupService.relationEdit(skuIds, addGroupIds, deleteGroupIds, operateId);			if (re != null && re.isSuccess()) {				result.setSuccessInfo(re.getSuccessInfo());				result.setSuccess(re.isSuccess());			} else {				result.setSuccess(re.isSuccess());				result.setErrorMessage(re.getErrorMessage());			}		}catch (Exception e){			log.error("商品分组修改异常", e);			result.setSuccessInfo("商品分组修改异常");			result.setSuccess(false);			result.setE(e);			return result;		}		return result;	}	@RequestMapping(value = "/skuInfo",method = RequestMethod.POST)	@ResponseBody	public Result<DownSkuInfoDTO> skuInfo(String groupIds,String currentPage,String pageSize) {		Result result=new Result();        return result;	}}