package com.songxiaocai.tradedata.web.controller.web;/* * Description:com.songxiaocai.tradedata.web.controller.web * @Date Create on 18:02 * @author <a href="mailto:yangyi@songxiaocai.com">yangyi</a> * @Version JDK 1.8 * @since version 1.0 Copyright 2019-02-21日SXC All Rights Reserved. */import com.songxiaocai.tradedata.tool.jedis.JedisTemplate;import lombok.extern.slf4j.Slf4j;import org.apache.commons.lang3.StringUtils;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Controller;import org.springframework.ui.Model;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestMethod;import org.springframework.web.bind.annotation.ResponseBody;import com.sxc.common.domain.result.Result;import org.springframework.web.servlet.ModelAndView;@Slf4j@Controller@RequestMapping("/test")public class TestController {	Integer SESSION_MAX_AGE = 60 * 60 * 24 * 30;	@Autowired	JedisTemplate jedisTemplate;	@RequestMapping(value = "/",method = RequestMethod.POST )	@ResponseBody	public Result<?> test(String para) {		Result result=new Result();		if(StringUtils.isAllBlank(para))		{			result.setSuccess(false);			result.setErrorMessage("请求参数为空！");			log.info("para: {}", para);			return result;		}		try{			result.setData("测试");			result.setSuccess(true);			return result;		}catch (Exception e){			log.error("para: {}", para, e);		}        return result;	}	@RequestMapping(value = "/test1" ,method = RequestMethod.GET)	@ResponseBody	public Result<?> test1() {		log.info("/test/test1");		Result result=new Result();		result.setData("测试1");		result.setSuccess(true);			return result;	}	@RequestMapping(value = "/test2" ,method = RequestMethod.GET)	@ResponseBody	public ModelAndView  test2(Model model) {		log.info("/test/test2");		ModelAndView mav = new ModelAndView();		model.addAttribute("测试2", "测试2");		mav.addObject(model);		mav.setViewName("index");		return mav;	}}